package com.kroslabs.recipemanager.ui.screens.addrecipe

import android.content.Context
import android.net.Uri
import androidx.lifecycle.SavedStateHandle
import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.kroslabs.recipemanager.data.local.PreferencesManager
import com.kroslabs.recipemanager.data.remote.RecipeExtractor
import com.kroslabs.recipemanager.data.repository.RecipeRepository
import com.kroslabs.recipemanager.domain.model.Ingredient
import com.kroslabs.recipemanager.domain.model.Language
import com.kroslabs.recipemanager.domain.model.Recipe
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.flow.*
import kotlinx.coroutines.launch
import kotlinx.coroutines.withContext
import java.io.BufferedReader
import java.io.InputStreamReader
import java.net.HttpURLConnection
import java.net.URL
import java.util.UUID
import javax.inject.Inject

enum class InputMode {
    CAMERA,
    PHOTOS,
    TEXT,
    URL,
    YOUTUBE
}

data class AddRecipeUiState(
    val inputMode: InputMode? = null,
    val textInput: String = "",
    val urlInput: String = "",
    val isExtracting: Boolean = false,
    val extractedRecipe: Recipe? = null,
    val editingRecipe: Recipe? = null,
    val language: Language = Language.ENGLISH,
    val error: String? = null,
    val savedRecipeId: String? = null,
    val isEditMode: Boolean = false
)

@HiltViewModel
class AddRecipeViewModel @Inject constructor(
    savedStateHandle: SavedStateHandle,
    private val recipeRepository: RecipeRepository,
    private val recipeExtractor: RecipeExtractor,
    private val preferencesManager: PreferencesManager
) : ViewModel() {

    private val recipeId: String? = savedStateHandle["recipeId"]

    private val _uiState = MutableStateFlow(AddRecipeUiState())
    val uiState: StateFlow<AddRecipeUiState> = _uiState.asStateFlow()

    init {
        viewModelScope.launch {
            preferencesManager.language.collect { language ->
                _uiState.update { it.copy(language = language) }
            }
        }

        if (recipeId != null) {
            loadRecipeForEdit(recipeId)
        }
    }

    private fun loadRecipeForEdit(id: String) {
        viewModelScope.launch {
            val recipe = recipeRepository.getRecipeById(id)
            if (recipe != null) {
                _uiState.update {
                    it.copy(
                        editingRecipe = recipe,
                        extractedRecipe = recipe,
                        isEditMode = true
                    )
                }
            }
        }
    }

    fun onInputModeSelect(mode: InputMode) {
        _uiState.update { it.copy(inputMode = mode, error = null) }
    }

    fun onTextInputChange(text: String) {
        _uiState.update { it.copy(textInput = text) }
    }

    fun onUrlInputChange(url: String) {
        _uiState.update { it.copy(urlInput = url) }
    }

    fun extractFromText() {
        val text = _uiState.value.textInput
        if (text.isBlank()) {
            _uiState.update { it.copy(error = "Please enter recipe text") }
            return
        }

        val apiKey = preferencesManager.getApiKey()
        if (apiKey.isNullOrBlank()) {
            _uiState.update { it.copy(error = "Please add your Claude API key in Settings") }
            return
        }

        viewModelScope.launch {
            _uiState.update { it.copy(isExtracting = true, error = null) }

            recipeExtractor.extractFromText(text, apiKey)
                .onSuccess { recipe ->
                    _uiState.update {
                        it.copy(
                            extractedRecipe = recipe,
                            editingRecipe = recipe,
                            isExtracting = false
                        )
                    }
                }
                .onFailure { e ->
                    _uiState.update {
                        it.copy(
                            error = e.message ?: "Failed to extract recipe",
                            isExtracting = false
                        )
                    }
                }
        }
    }

    fun extractFromUrl() {
        val url = _uiState.value.urlInput
        if (url.isBlank()) {
            _uiState.update { it.copy(error = "Please enter a URL") }
            return
        }

        val apiKey = preferencesManager.getApiKey()
        if (apiKey.isNullOrBlank()) {
            _uiState.update { it.copy(error = "Please add your Claude API key in Settings") }
            return
        }

        viewModelScope.launch {
            _uiState.update { it.copy(isExtracting = true, error = null) }

            try {
                val htmlContent = fetchUrl(url)
                recipeExtractor.extractFromUrl(url, htmlContent, apiKey)
                    .onSuccess { recipe ->
                        _uiState.update {
                            it.copy(
                                extractedRecipe = recipe,
                                editingRecipe = recipe,
                                isExtracting = false
                            )
                        }
                    }
                    .onFailure { e ->
                        _uiState.update {
                            it.copy(
                                error = e.message ?: "Failed to extract recipe",
                                isExtracting = false
                            )
                        }
                    }
            } catch (e: Exception) {
                _uiState.update {
                    it.copy(
                        error = "Failed to fetch URL: ${e.message}",
                        isExtracting = false
                    )
                }
            }
        }
    }

    fun extractFromImage(context: Context, imageUri: Uri) {
        val apiKey = preferencesManager.getApiKey()
        if (apiKey.isNullOrBlank()) {
            _uiState.update { it.copy(error = "Please add your Claude API key in Settings") }
            return
        }

        viewModelScope.launch {
            _uiState.update { it.copy(isExtracting = true, error = null) }

            try {
                val inputStream = context.contentResolver.openInputStream(imageUri)
                val bytes = inputStream?.readBytes() ?: throw Exception("Failed to read image")
                inputStream.close()

                val mimeType = context.contentResolver.getType(imageUri) ?: "image/jpeg"

                recipeExtractor.extractFromImage(bytes, mimeType, apiKey)
                    .onSuccess { recipe ->
                        _uiState.update {
                            it.copy(
                                extractedRecipe = recipe,
                                editingRecipe = recipe,
                                isExtracting = false
                            )
                        }
                    }
                    .onFailure { e ->
                        _uiState.update {
                            it.copy(
                                error = e.message ?: "Failed to extract recipe",
                                isExtracting = false
                            )
                        }
                    }
            } catch (e: Exception) {
                _uiState.update {
                    it.copy(
                        error = "Failed to process image: ${e.message}",
                        isExtracting = false
                    )
                }
            }
        }
    }

    private suspend fun fetchUrl(urlString: String): String = withContext(Dispatchers.IO) {
        val url = URL(urlString)
        val connection = url.openConnection() as HttpURLConnection
        connection.requestMethod = "GET"
        connection.setRequestProperty("User-Agent", "Mozilla/5.0")
        connection.connectTimeout = 10000
        connection.readTimeout = 10000

        val reader = BufferedReader(InputStreamReader(connection.inputStream))
        val content = reader.readText()
        reader.close()
        connection.disconnect()
        content
    }

    fun updateTitle(language: Language, title: String) {
        _uiState.update { state ->
            val recipe = state.editingRecipe ?: return@update state
            val updated = when (language) {
                Language.ENGLISH -> recipe.copy(titleEnglish = title)
                Language.SWEDISH -> recipe.copy(titleSwedish = title)
                Language.ROMANIAN -> recipe.copy(titleRomanian = title)
            }
            state.copy(editingRecipe = updated)
        }
    }

    fun updateServings(servings: Int?) {
        _uiState.update { state ->
            val recipe = state.editingRecipe ?: return@update state
            state.copy(editingRecipe = recipe.copy(servings = servings))
        }
    }

    fun updatePrepTime(time: String?) {
        _uiState.update { state ->
            val recipe = state.editingRecipe ?: return@update state
            state.copy(editingRecipe = recipe.copy(prepTime = time?.takeIf { it.isNotBlank() }))
        }
    }

    fun updateCookTime(time: String?) {
        _uiState.update { state ->
            val recipe = state.editingRecipe ?: return@update state
            state.copy(editingRecipe = recipe.copy(cookTime = time?.takeIf { it.isNotBlank() }))
        }
    }

    fun addIngredient(language: Language, text: String) {
        if (text.isBlank()) return
        _uiState.update { state ->
            val recipe = state.editingRecipe ?: return@update state
            val ingredient = Ingredient(
                id = UUID.randomUUID().toString(),
                text = text,
                name = text
            )
            val updated = when (language) {
                Language.ENGLISH -> recipe.copy(ingredientsEnglish = recipe.ingredientsEnglish + ingredient)
                Language.SWEDISH -> recipe.copy(ingredientsSwedish = recipe.ingredientsSwedish + ingredient)
                Language.ROMANIAN -> recipe.copy(ingredientsRomanian = recipe.ingredientsRomanian + ingredient)
            }
            state.copy(editingRecipe = updated)
        }
    }

    fun removeIngredient(language: Language, ingredientId: String) {
        _uiState.update { state ->
            val recipe = state.editingRecipe ?: return@update state
            val updated = when (language) {
                Language.ENGLISH -> recipe.copy(
                    ingredientsEnglish = recipe.ingredientsEnglish.filter { it.id != ingredientId }
                )
                Language.SWEDISH -> recipe.copy(
                    ingredientsSwedish = recipe.ingredientsSwedish.filter { it.id != ingredientId }
                )
                Language.ROMANIAN -> recipe.copy(
                    ingredientsRomanian = recipe.ingredientsRomanian.filter { it.id != ingredientId }
                )
            }
            state.copy(editingRecipe = updated)
        }
    }

    fun addInstruction(language: Language, text: String) {
        if (text.isBlank()) return
        _uiState.update { state ->
            val recipe = state.editingRecipe ?: return@update state
            val updated = when (language) {
                Language.ENGLISH -> recipe.copy(instructionsEnglish = recipe.instructionsEnglish + text)
                Language.SWEDISH -> recipe.copy(instructionsSwedish = recipe.instructionsSwedish + text)
                Language.ROMANIAN -> recipe.copy(instructionsRomanian = recipe.instructionsRomanian + text)
            }
            state.copy(editingRecipe = updated)
        }
    }

    fun removeInstruction(language: Language, index: Int) {
        _uiState.update { state ->
            val recipe = state.editingRecipe ?: return@update state
            val updated = when (language) {
                Language.ENGLISH -> recipe.copy(
                    instructionsEnglish = recipe.instructionsEnglish.filterIndexed { i, _ -> i != index }
                )
                Language.SWEDISH -> recipe.copy(
                    instructionsSwedish = recipe.instructionsSwedish.filterIndexed { i, _ -> i != index }
                )
                Language.ROMANIAN -> recipe.copy(
                    instructionsRomanian = recipe.instructionsRomanian.filterIndexed { i, _ -> i != index }
                )
            }
            state.copy(editingRecipe = updated)
        }
    }

    fun toggleTag(tag: String) {
        _uiState.update { state ->
            val recipe = state.editingRecipe ?: return@update state
            val newTags = if (recipe.tags.contains(tag)) {
                recipe.tags - tag
            } else {
                recipe.tags + tag
            }
            state.copy(editingRecipe = recipe.copy(tags = newTags))
        }
    }

    fun updateNotes(language: Language, notes: String) {
        _uiState.update { state ->
            val recipe = state.editingRecipe ?: return@update state
            val notesValue = notes.takeIf { it.isNotBlank() }
            val updated = when (language) {
                Language.ENGLISH -> recipe.copy(notesEnglish = notesValue)
                Language.SWEDISH -> recipe.copy(notesSwedish = notesValue)
                Language.ROMANIAN -> recipe.copy(notesRomanian = notesValue)
            }
            state.copy(editingRecipe = updated)
        }
    }

    fun saveRecipe() {
        val recipe = _uiState.value.editingRecipe
        if (recipe == null) {
            _uiState.update { it.copy(error = "No recipe to save") }
            return
        }

        if (recipe.titleEnglish.isBlank()) {
            _uiState.update { it.copy(error = "Recipe title is required") }
            return
        }

        if (recipe.ingredientsEnglish.isEmpty()) {
            _uiState.update { it.copy(error = "At least one ingredient is required") }
            return
        }

        if (recipe.instructionsEnglish.isEmpty()) {
            _uiState.update { it.copy(error = "At least one instruction is required") }
            return
        }

        viewModelScope.launch {
            if (_uiState.value.isEditMode) {
                recipeRepository.updateRecipe(recipe)
            } else {
                recipeRepository.insertRecipe(recipe)
            }
            _uiState.update { it.copy(savedRecipeId = recipe.id) }
        }
    }

    fun clearError() {
        _uiState.update { it.copy(error = null) }
    }

    fun createManualRecipe() {
        val newRecipe = Recipe(
            id = UUID.randomUUID().toString(),
            titleEnglish = "",
            titleSwedish = "",
            titleRomanian = ""
        )
        _uiState.update {
            it.copy(
                extractedRecipe = newRecipe,
                editingRecipe = newRecipe
            )
        }
    }
}

package com.kroslabs.recipemanager.ui.screens.mealplan

import android.content.Context
import android.content.Intent
import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.kroslabs.recipemanager.data.local.PreferencesManager
import com.kroslabs.recipemanager.data.repository.MealPlanRepository
import com.kroslabs.recipemanager.data.repository.RecipeRepository
import com.kroslabs.recipemanager.domain.model.*
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.flow.*
import kotlinx.coroutines.launch
import java.time.LocalDate
import java.time.format.DateTimeFormatter
import java.time.format.FormatStyle
import java.util.Locale
import javax.inject.Inject

data class MealPlanUiState(
    val mealPlans: Map<LocalDate, MealPlan> = emptyMap(),
    val recipes: List<Recipe> = emptyList(),
    val language: Language = Language.ENGLISH,
    val selectedDate: LocalDate = LocalDate.now(),
    val isLoading: Boolean = true,
    val showRecipeSelector: Boolean = false,
    val selectingSlot: MealType? = null,
    val showShareDialog: Boolean = false,
    val searchQuery: String = ""
)

@HiltViewModel
class MealPlanViewModel @Inject constructor(
    private val mealPlanRepository: MealPlanRepository,
    private val recipeRepository: RecipeRepository,
    private val preferencesManager: PreferencesManager
) : ViewModel() {

    private val _uiState = MutableStateFlow(MealPlanUiState())
    val uiState: StateFlow<MealPlanUiState> = _uiState.asStateFlow()

    private val dateRange: Pair<LocalDate, LocalDate>
        get() {
            val today = LocalDate.now()
            return today.minusDays(90) to today.plusDays(180)
        }

    init {
        viewModelScope.launch {
            combine(
                mealPlanRepository.getMealPlansByDateRange(dateRange.first, dateRange.second),
                recipeRepository.getAllRecipes(),
                preferencesManager.language
            ) { mealPlans, recipes, language ->
                val mealPlanMap = mealPlans.associateBy { LocalDate.parse(it.date) }
                _uiState.update { state ->
                    state.copy(
                        mealPlans = mealPlanMap,
                        recipes = recipes,
                        language = language,
                        isLoading = false
                    )
                }
            }.collect()
        }
    }

    fun onDateSelect(date: LocalDate) {
        _uiState.update { it.copy(selectedDate = date) }
    }

    fun onSlotClick(date: LocalDate, mealType: MealType) {
        _uiState.update {
            it.copy(
                selectedDate = date,
                selectingSlot = mealType,
                showRecipeSelector = true,
                searchQuery = ""
            )
        }
    }

    fun onRecipeSelect(recipe: Recipe) {
        val date = _uiState.value.selectedDate
        val mealType = _uiState.value.selectingSlot ?: return

        viewModelScope.launch {
            val existingPlan = _uiState.value.mealPlans[date]
                ?: mealPlanRepository.getOrCreateMealPlanForDate(date)

            val updatedPlan = when (mealType) {
                MealType.LUNCH -> existingPlan.copy(
                    lunchSlot = existingPlan.lunchSlot.copy(
                        recipeId = recipe.id,
                        recipeName = recipe.getTitle(_uiState.value.language)
                    )
                )
                MealType.DINNER -> existingPlan.copy(
                    dinnerSlot = existingPlan.dinnerSlot.copy(
                        recipeId = recipe.id,
                        recipeName = recipe.getTitle(_uiState.value.language)
                    )
                )
            }

            mealPlanRepository.updateMealPlan(updatedPlan)
            _uiState.update {
                it.copy(
                    showRecipeSelector = false,
                    selectingSlot = null
                )
            }
        }
    }

    fun onRemoveMeal(date: LocalDate, mealType: MealType) {
        viewModelScope.launch {
            val existingPlan = _uiState.value.mealPlans[date] ?: return@launch

            val updatedPlan = when (mealType) {
                MealType.LUNCH -> existingPlan.copy(
                    lunchSlot = existingPlan.lunchSlot.copy(recipeId = null, recipeName = null)
                )
                MealType.DINNER -> existingPlan.copy(
                    dinnerSlot = existingPlan.dinnerSlot.copy(recipeId = null, recipeName = null)
                )
            }

            mealPlanRepository.updateMealPlan(updatedPlan)
        }
    }

    fun onSwapMeals(date: LocalDate) {
        viewModelScope.launch {
            val existingPlan = _uiState.value.mealPlans[date] ?: return@launch

            val updatedPlan = existingPlan.copy(
                lunchSlot = existingPlan.lunchSlot.copy(
                    recipeId = existingPlan.dinnerSlot.recipeId,
                    recipeName = existingPlan.dinnerSlot.recipeName
                ),
                dinnerSlot = existingPlan.dinnerSlot.copy(
                    recipeId = existingPlan.lunchSlot.recipeId,
                    recipeName = existingPlan.lunchSlot.recipeName
                )
            )

            mealPlanRepository.updateMealPlan(updatedPlan)
        }
    }

    fun onDismissRecipeSelector() {
        _uiState.update {
            it.copy(
                showRecipeSelector = false,
                selectingSlot = null,
                searchQuery = ""
            )
        }
    }

    fun onSearchQueryChange(query: String) {
        _uiState.update { it.copy(searchQuery = query) }
    }

    fun getFilteredRecipes(): List<Recipe> {
        val query = _uiState.value.searchQuery.lowercase()
        if (query.isBlank()) return _uiState.value.recipes

        return _uiState.value.recipes.filter { recipe ->
            recipe.titleEnglish.lowercase().contains(query) ||
            recipe.titleSwedish.lowercase().contains(query) ||
            recipe.titleRomanian.lowercase().contains(query)
        }
    }

    fun onShowShareDialog(show: Boolean) {
        _uiState.update { it.copy(showShareDialog = show) }
    }

    fun shareMealPlan(
        context: Context,
        startDate: LocalDate,
        endDate: LocalDate,
        includeShoppingList: Boolean
    ) {
        val language = _uiState.value.language
        val mealPlans = _uiState.value.mealPlans
        val recipes = _uiState.value.recipes.associateBy { it.id }

        val locale = when (language) {
            Language.ENGLISH -> Locale.ENGLISH
            Language.SWEDISH -> Locale("sv")
            Language.ROMANIAN -> Locale("ro")
        }

        val dateFormatter = DateTimeFormatter.ofLocalizedDate(FormatStyle.FULL).withLocale(locale)

        val text = buildString {
            appendLine("Meal Plan: ${startDate.format(DateTimeFormatter.ISO_LOCAL_DATE)} - ${endDate.format(DateTimeFormatter.ISO_LOCAL_DATE)}")
            appendLine()

            var currentDate = startDate
            val ingredientCounts = mutableMapOf<String, Int>()

            while (!currentDate.isAfter(endDate)) {
                val plan = mealPlans[currentDate]

                appendLine(currentDate.format(dateFormatter))

                val lunchName = plan?.lunchSlot?.recipeId?.let { id ->
                    recipes[id]?.getTitle(language)
                } ?: plan?.lunchSlot?.recipeName ?: "-"

                val dinnerName = plan?.dinnerSlot?.recipeId?.let { id ->
                    recipes[id]?.getTitle(language)
                } ?: plan?.dinnerSlot?.recipeName ?: "-"

                appendLine("\uD83E\uDD57 Lunch: $lunchName")
                appendLine("\uD83C\uDF7Dï¸ Dinner: $dinnerName")
                appendLine()

                if (includeShoppingList) {
                    plan?.lunchSlot?.recipeId?.let { id ->
                        recipes[id]?.getIngredients(language)?.forEach { ingredient ->
                            ingredientCounts[ingredient.name] = (ingredientCounts[ingredient.name] ?: 0) + 1
                        }
                    }
                    plan?.dinnerSlot?.recipeId?.let { id ->
                        recipes[id]?.getIngredients(language)?.forEach { ingredient ->
                            ingredientCounts[ingredient.name] = (ingredientCounts[ingredient.name] ?: 0) + 1
                        }
                    }
                }

                currentDate = currentDate.plusDays(1)
            }

            if (includeShoppingList && ingredientCounts.isNotEmpty()) {
                appendLine("---")
                appendLine("Shopping List:")
                ingredientCounts.entries.sortedBy { it.key }.forEach { (name, count) ->
                    if (count > 1) {
                        appendLine("- $name ($count recipes)")
                    } else {
                        appendLine("- $name")
                    }
                }
            }
        }

        val intent = Intent(Intent.ACTION_SEND).apply {
            type = "text/plain"
            putExtra(Intent.EXTRA_TEXT, text)
        }
        context.startActivity(Intent.createChooser(intent, "Share Meal Plan"))

        _uiState.update { it.copy(showShareDialog = false) }
    }

    fun getDateRange(): Pair<LocalDate, LocalDate> = dateRange
}

package com.kroslabs.recipemanager.ui.screens.settings

import android.content.Context
import android.net.Uri
import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.kroslabs.recipemanager.data.local.PreferencesManager
import com.kroslabs.recipemanager.data.repository.MealPlanRepository
import com.kroslabs.recipemanager.data.repository.RecipeRepository
import com.kroslabs.recipemanager.domain.model.Language
import com.kroslabs.recipemanager.domain.model.MealPlan
import com.kroslabs.recipemanager.domain.model.Recipe
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.flow.*
import kotlinx.coroutines.launch
import kotlinx.serialization.Serializable
import kotlinx.serialization.encodeToString
import kotlinx.serialization.json.Json
import java.io.BufferedReader
import java.io.InputStreamReader
import java.time.Instant
import javax.inject.Inject

data class SettingsUiState(
    val apiKey: String = "",
    val language: Language = Language.ENGLISH,
    val cloudSyncEnabled: Boolean = false,
    val lastSync: Long? = null,
    val isSyncing: Boolean = false,
    val showLanguageDialog: Boolean = false,
    val showExportDialog: Boolean = false,
    val message: String? = null
)

@HiltViewModel
class SettingsViewModel @Inject constructor(
    private val preferencesManager: PreferencesManager,
    private val recipeRepository: RecipeRepository,
    private val mealPlanRepository: MealPlanRepository
) : ViewModel() {

    private val json = Json {
        prettyPrint = true
        ignoreUnknownKeys = true
    }

    private val _uiState = MutableStateFlow(SettingsUiState())
    val uiState: StateFlow<SettingsUiState> = _uiState.asStateFlow()

    init {
        _uiState.update { it.copy(apiKey = preferencesManager.getApiKey() ?: "") }

        viewModelScope.launch {
            combine(
                preferencesManager.language,
                preferencesManager.cloudSyncEnabled,
                preferencesManager.lastSync
            ) { language, syncEnabled, lastSync ->
                _uiState.update { state ->
                    state.copy(
                        language = language,
                        cloudSyncEnabled = syncEnabled,
                        lastSync = lastSync
                    )
                }
            }.collect()
        }
    }

    fun onApiKeyChange(apiKey: String) {
        _uiState.update { it.copy(apiKey = apiKey) }
    }

    fun saveApiKey() {
        val apiKey = _uiState.value.apiKey.trim()
        preferencesManager.setApiKey(apiKey.takeIf { it.isNotBlank() })
        _uiState.update { it.copy(message = "API key saved") }
    }

    fun onLanguageChange(language: Language) {
        viewModelScope.launch {
            preferencesManager.setLanguage(language)
            _uiState.update { it.copy(showLanguageDialog = false) }
        }
    }

    fun onCloudSyncToggle(enabled: Boolean) {
        viewModelScope.launch {
            preferencesManager.setCloudSyncEnabled(enabled)
        }
    }

    fun onSyncNow() {
        viewModelScope.launch {
            _uiState.update { it.copy(isSyncing = true) }
            // Cloud sync would go here - for now just update timestamp
            preferencesManager.setLastSync(System.currentTimeMillis())
            _uiState.update { it.copy(isSyncing = false, message = "Sync completed") }
        }
    }

    fun onShowLanguageDialog(show: Boolean) {
        _uiState.update { it.copy(showLanguageDialog = show) }
    }

    fun onShowExportDialog(show: Boolean) {
        _uiState.update { it.copy(showExportDialog = show) }
    }

    fun clearMessage() {
        _uiState.update { it.copy(message = null) }
    }

    suspend fun exportData(context: Context, uri: Uri, type: ExportType) {
        viewModelScope.launch {
            try {
                val recipes = recipeRepository.getAllRecipes().first()
                val mealPlans = mealPlanRepository.getAllMealPlans().first()

                val exportData = when (type) {
                    ExportType.ALL -> ExportData(
                        type = "complete",
                        recipes = recipes,
                        mealPlans = mealPlans
                    )
                    ExportType.RECIPES -> ExportData(
                        type = "recipe_collection",
                        recipes = recipes
                    )
                    ExportType.MEAL_PLANS -> ExportData(
                        type = "meal_plan",
                        mealPlans = mealPlans
                    )
                }

                val jsonString = json.encodeToString(exportData)

                context.contentResolver.openOutputStream(uri)?.use { outputStream ->
                    outputStream.write(jsonString.toByteArray())
                }

                _uiState.update { it.copy(message = "Export successful", showExportDialog = false) }
            } catch (e: Exception) {
                _uiState.update { it.copy(message = "Export failed: ${e.message}") }
            }
        }
    }

    suspend fun importData(context: Context, uri: Uri) {
        viewModelScope.launch {
            try {
                val jsonString = context.contentResolver.openInputStream(uri)?.use { inputStream ->
                    BufferedReader(InputStreamReader(inputStream)).readText()
                } ?: throw Exception("Failed to read file")

                val importData = json.decodeFromString<ExportData>(jsonString)

                importData.recipes?.let { recipes ->
                    recipeRepository.insertRecipes(recipes)
                }

                importData.mealPlans?.let { mealPlans ->
                    mealPlanRepository.insertMealPlans(mealPlans)
                }

                val recipeCount = importData.recipes?.size ?: 0
                val mealPlanCount = importData.mealPlans?.size ?: 0
                _uiState.update {
                    it.copy(message = "Imported $recipeCount recipes and $mealPlanCount meal plans")
                }
            } catch (e: Exception) {
                _uiState.update { it.copy(message = "Import failed: ${e.message}") }
            }
        }
    }
}

enum class ExportType {
    ALL,
    RECIPES,
    MEAL_PLANS
}

@Serializable
data class ExportData(
    val version: String = "1.0",
    val exportDate: String = Instant.now().toString(),
    val type: String,
    val recipes: List<Recipe>? = null,
    val mealPlans: List<MealPlan>? = null
)

